function initGallery(){const e=document.querySelector(".gallery");for(let t=0;t<25;t++){const n=document.createElement("img");n.classList.add("gallery__item"),n.src=`src/${t}.jpeg`,e.append(n)}}function initCountdown(){const e=864e5,t=36e5,n=document.querySelector(".js-countdown"),o=new Date,r=new Date("September 14, 2024 020:00:00")-o;let i,l,a;i=Math.floor(r/e),l=Math.floor((r-i*e)/t),a=Math.floor((r-i*e-l*t)/6e4),n.innerText=`${i} day${1==i?"":"s"} ${l} hour${1==l?"":"s"} ${a} minute${1==a?"":"s"}`}window.addEventListener("DOMContentLoaded",(()=>{initGallery(),initCountdown()}));